#include <edit/asdxLocalization.h>


namespace {

//-----------------------------------------------------------------------------
// Constant Value.
//-----------------------------------------------------------------------------
static asdx::LANGUAGE_TYPE  g_Type = asdx::LANGUAGE_JP;

} // namespace

namespace asdx {

//-----------------------------------------------------------------------------
//      現在の言語タイプを取得します.
//-----------------------------------------------------------------------------
LANGUAGE_TYPE GetCurrentLanguageType()
{ return g_Type; }

//-----------------------------------------------------------------------------
//      現在の言語タイプを設定します.
//-----------------------------------------------------------------------------
void SetCurrentLanguageType(LANGUAGE_TYPE type)
{ g_Type = type; }

} // namespaceasdx
